import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import ReactMarkdown from 'react-markdown';

interface Message {
  id: string;
  content: string;
  role: 'user' | 'assistant';
  timestamp: Date;
}

interface StreamResponse {
  type: 'content' | 'error' | 'done';
  content?: string;
  conversation_id?: string;
  message_id?: string;
}

const App: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [conversationId] = useState(() => 
    Math.random().toString(36).substring(2, 15)
  );
  const [isConnected, setIsConnected] = useState(true);
  const [tokenStats, setTokenStats] = useState({ total_messages: 0, total_conversations: 0 });
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const addMessage = (content: string, role: 'user' | 'assistant') => {
    const newMessage: Message = {
      id: Math.random().toString(36).substring(2, 15),
      content,
      role,
      timestamp: new Date()
    };
    setMessages(prev => [...prev, newMessage]);
    return newMessage.id;
  };

  const updateLastMessage = (content: string) => {
    setMessages(prev => {
      const newMessages = [...prev];
      if (newMessages.length > 0) {
        newMessages[newMessages.length - 1] = {
          ...newMessages[newMessages.length - 1],
          content
        };
      }
      return newMessages;
    });
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage = inputValue.trim();
    setInputValue('');
    setIsLoading(true);

    addMessage(userMessage, 'user');

    try {
      const response = await fetch('http://localhost:8000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: userMessage,
          conversation_id: conversationId
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const reader = response.body?.getReader();
      if (!reader) {
        throw new Error('No response body reader available');
      }

      let assistantMessageId: string | null = null;
      let accumulatedContent = '';
      const decoder = new TextDecoder();

      let buffer = '';
      try {
        while (true) {
          const { done, value } = await reader.read();

          if (done) {
            setIsLoading(false);
            break;
          }

          buffer += decoder.decode(value, { stream: true });
          const lines = buffer.split('\n');

          // Keep the last line in buffer (might be incomplete)
          buffer = lines.pop() || '';

          for (const line of lines) {
            if (line.includes('{"type":')) {
              try {
                // Find the JSON part (skip any "data:" prefixes)
                const jsonStart = line.indexOf('{"type":');
                const jsonStr = line.substring(jsonStart).trim();
                if (!jsonStr) continue;
                const data = JSON.parse(jsonStr);

                if (data.type === 'content' && data.content) {
                  accumulatedContent += data.content;

                  if (!assistantMessageId) {
                    assistantMessageId = addMessage(accumulatedContent, 'assistant');
                  } else {
                    updateLastMessage(accumulatedContent);
                  }
                } else if (data.type === 'done') {
                  setIsLoading(false);
                  return;
                }
              } catch (parseError) {
                console.error('Error parsing SSE data:', parseError);
              }
            }
          }
        }
      } finally {
        reader.releaseLock();
      }

    } catch (error) {
      console.error('Error sending message:', error);
      addMessage('Sorry, I encountered a connection error.', 'assistant');
      setIsLoading(false);
      setIsConnected(false);

      // Try to reconnect after a delay
      setTimeout(() => {
        setIsConnected(true);
      }, 3000);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const clearConversation = async () => {
    try {
      // Call backend to clear conversation
      await fetch(`http://localhost:8000/api/conversations/${conversationId}`, {
        method: 'DELETE'
      });
    } catch (error) {
      console.warn('Failed to clear conversation on backend:', error);
    }

    setMessages([]);
  };

  const trySampleQuestion = (question: string) => {
    setInputValue(question);
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>PDF Chat Assistant</h1>
        <p>Ask questions about accessible travel laws and regulations</p>
        <div className="header-controls">
          <span className={`connection-status ${isConnected ? 'connected' : 'disconnected'}`}>
            {isConnected ? 'üü¢ Connected' : 'üî¥ Disconnected'}
          </span>
          <span className="token-stats">
            üìä Messages: {tokenStats.total_messages} | Conversations: {tokenStats.total_conversations}
          </span>
          <button 
            onClick={clearConversation}
            className="clear-button"
            disabled={messages.length === 0}
          >
            Clear Chat
          </button>
        </div>
      </header>

      <main className="chat-container">
        <div className="messages-container">
          {messages.length === 0 ? (
            <div className="welcome-message">
              <h2>üëã Welcome!</h2>
              <p>I'm here to answer questions about accessible travel laws and regulations based on the loaded PDF document.</p>
              <div className="sample-questions">
                <p><strong>Try asking:</strong></p>
                <ul>
                  {[
                    "What is the ADA?",
                    "Tell me about accessibility laws in Canada",
                    "How many people globally have disabilities?",
                    "What does the document say about transportation?"
                  ].map((question, index) => (
                    <li key={index} onClick={() => trySampleQuestion(question)}>
                      "{question}"
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          ) : (
            messages.map((message) => (
              <div
                key={message.id}
                className={`message ${message.role === 'user' ? 'user-message' : 'assistant-message'}`}
              >
                <div className="message-header">
                  <span className="message-role">
                    {message.role === 'user' ? 'üë§ You' : 'ü§ñ Assistant'}
                  </span>
                  <span className="message-time">
                    {message.timestamp.toLocaleTimeString()}
                  </span>
                </div>
                <div className="message-content">
                  {message.role === 'assistant' ? (
                    <ReactMarkdown>{message.content}</ReactMarkdown>
                  ) : (
                    message.content
                  )}
                </div>
              </div>
            ))
          )}

          {isLoading && (
            <div className="message assistant-message loading">
              <div className="message-header">
                <span className="message-role">ü§ñ Assistant</span>
                <span className="message-time">typing...</span>
              </div>
              <div className="message-content">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        <div className="input-container">
          <div className="input-wrapper">
            <textarea
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask a question about accessible travel..."
              disabled={isLoading || !isConnected}
              rows={1}
              className="message-input"
            />
            <button
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || isLoading || !isConnected}
              className="send-button"
            >
              {isLoading ? '‚è≥' : 'üì§'}
            </button>
          </div>
          <div className="input-hint">
            Press Enter to send, Shift+Enter for new line
            {!isConnected && ' ‚Ä¢ Backend connection lost'}
          </div>
        </div>
      </main>
    </div>
  );
};

export default App;
